import React, { useState } from "react";

const [title, setTopic] = useState("")
const [text, setText] = useState("")

async function handleThread(e) {
    e.preventDefault();
    fetch("http://localhost:4000/thread", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ 
                            topic: formData.get('title'),
                            text: formData.get('text')
                            })
    })
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => console.error(error))
  }

function ThreadMaker() {
    return (
        <div className="ThreadMaker">
            <form onSubmit={handleThread}>
            <input type="text" placeholder="Title" name="title"/>
            <input type="text" placeholder="Post text" name="text"/>
            <button type="submit">Post Thread</button>
            </form>
        </div>
    );
}

export default ThreadMaker;
