import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import './Thread.css';

function Thread() {
  const { id } = useParams();
  const [Thread, setThread] = useState(null);
  const [spinner, setSpinner] = useState(false);

  useEffect(() => {
    setSpinner(true);
    fetch(`http://localhost:4000/thread/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setSpinner(false);
        console.log(data);
        if (data.message === "404") {
          return;
        }
        setThread(data);
      })
      .catch((error) => console.error(error));
  }, [id]);

const formatDate = (dateString) => {
    const date = new Date(dateString);
    const formattedDate = `${date.getDate()}.${date.getMonth() + 1}.${date.getFullYear()} klo ${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}`;
    return formattedDate;
};

  return (
    <div className="container">
      {spinner ? (
        <div className="spinner"></div>
      ) : Thread ? (
        <>
        <p>OP</p>
        <p>{Thread.ID}<span>{formatDate(Thread.created_at)}</span></p>
        <p>{Thread.title}</p>
        <p>{Thread.text}</p>
        </>
      ) : (
        <>
          <h2>404 Thread not found</h2>
        </>
      )}
    </div>
  );
}

export default Thread;
