import './App.css';
import React, { useState, useEffect, useRef } from "react";

function PostReply({ postId }) {

    const prevPostIdRef = useRef(null);

    useEffect(() => {
        if (postId && postId !== prevPostIdRef.current) {
          setText((prevText) => `${prevText} >>${postId}`);
          prevPostIdRef.current = postId;
        }
      }, [postId]);

    const [text, setText] = useState("");

    function handleReply (e) {
        e.preventDefault()
        console.log(text)
    }

    return (
    <div className="PostReply">
        <form onSubmit={handleReply}>
            <input type="text" placeholder="Post text" name="text" value={text} onChange={(e) => setText(e.target.value)} />
            <button type="submit">Post Reply</button>
        </form>
    </div>
    );
}

export default PostReply;
