import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import './Thread.css';

function Thread() {
  const { id } = useParams();
  const [Thread, setThread] = useState(null);
  const [spinner, setSpinner] = useState(false);

  useEffect(() => {
    setSpinner(true);
    fetch(`http://localhost:4000/thread/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setSpinner(false);
        console.log(data);
        if (data.message === "404") {
          return;
        }
        setThread(data);
      })
      .catch((error) => console.error(error));
  }, [id]);

const formatDate = (datesString) => {
    const datesArr = datesString.split("T")
    const dateArr =  datesArr[0].split("-")
    const clockTime = datesArr[1].split(".")
    const clockTimeNew = clockTime[0].replace(":", ".")
    const time = `${dateArr[2]}.${dateArr[1]}.${dateArr[0]} klo ${clockTimeNew}`
    return time
};

  return (
    <div className="container">
      {spinner ? (
        <div className="spinner"></div>
      ) : Thread ? (
        <>
        <div className="postHeader">
        <p>{Thread.title}</p>
        <p>Anonymous</p>
        <p>{Thread.ID}<span> {formatDate(Thread.created_at)}</span></p>
        </div>
        <div className="postText">
        <p>{Thread.text}</p>
        </div>
        </>
      ) : (
        <>
          <h2>404 Thread not found</h2>
        </>
      )}
    </div>
  );
}

export default Thread;
